/**
 *
 * @param {Object} props
 * @param {'original' | 'black' | 'white'} [props.iconStyle='original']
 */
export function ReadrIcon({ iconStyle }) {
  switch (iconStyle) {
    case 'original':
      return <DefaultLogo />
    case 'black':
      return <BlackLogo />
    case 'white':
      return <WhiteLogo />
    default:
      break
  }
}

